def orders_subgroup(c, g, p, factor):
    lst = []
    for x in factor:
        C, G = pow(c, (p - 1)//x, p), pow(g, (p - 1)//x, p)
        for i in range(x):
            if pow(G, i, p) == C:
                lst.append(i)
                break
    return lst

def order(g, n, n_phi_Euler_factor, n_phi_Euler):
    for q in n_phi_Euler_factor[::-1]:
        Ord = n_phi_Euler//q
        if pow(g, Ord, n) == 1:
            return Ord
    return n_phi_Euler

